mapper.select:
select ID
  from USER
 where ID=#{id}

select EMAIL
  from USER
 where EMAIL=#{email}

select NICKNAME
  from USER
 where NICKNAME=#{nickname}

select NICKNAME
  from USER
 where ID=#{id}

select PASSWORD
  from USER
 where ID=#{id}

select USER_ID
  from USER
 where EMAIL=#{user}

select ID 
  from USER
 where EMAIL = #{email}

select NICKNAME
  from USER
 where EMAIL = #{email} 
   and ID = #{id}

mapper.update:

update USER
  set SUBSCRIBE_YN="Y", EXPIRATION_DATE=(date_add(NOW(),interval ${months} month))
where USER_ID = (select U.USER_ID from (select USER_ID from USER where ID="${ID}") AS U);

update USER
   set PASSWORD=#{password}
 where ID = #{id}

mapper.select:
select ID, EMAIL, NICKNAME, PHONE_NUM
  from USER
 where ID=#{user_id}
